import { all, call } from 'redux-saga/effects'

import { fetchCollectionsStartSaga } from './shop/shop.sagas'
import { onGoogleSignInStartSaga } from './user/user.sagas'

export function* rootSaga() {
  yield all([call(fetchCollectionsStartSaga), call(onGoogleSignInStartSaga)])
}
