import React,{Component} from 'react'
import {Route} from 'react-router-dom'
import {createStructuredSelector} from 'reselect'
import CollectionPage from '../collection/collection.comopnent'
import {connect} from 'react-redux'
import CollectionsOverview from '../../components/collections-overview/collections-overview'


import { fetchCollectionsStartAsync } from '../../redux/shop/shop.actions'
import {selectIsCollectionFetcing,selectIsCollectionLoaded} from '../../redux/shop/shop.selectors'

import WithSpinner from '../../components/with-spinner/with-spinner.component'


const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview)
const CollectiosnPageWithspinner = WithSpinner(CollectionPage)

class  ShopPage extends Component{  


    componentDidMount(){
      const {fetchCollectionsStartAsync} = this.props
      fetchCollectionsStartAsync()
    }

    render(){
        const {match,isCollectionFetcing,isCollectionLoaded} = this.props
        return(
            <div className='shop-page'>
                <Route exact path = {`${match.path}`} render={(props)=><CollectionsOverviewWithSpinner isLoading={isCollectionFetcing}{...props}/>}/>
                <Route path={`${match.path}/:CollectionId`} render={(props)=><CollectiosnPageWithspinner isLoading={!isCollectionLoaded}{...props}/>}/>
            </div>
)
    }
}

const mapStateToProps = createStructuredSelector({
    isCollectionFetcing: selectIsCollectionFetcing,
    isCollectionLoaded: selectIsCollectionLoaded
})

const mapDispatchToProps=(dispatch)=>({
    fetchCollectionsStartAsync: ()=>dispatch(fetchCollectionsStartAsync())
})


export default connect(mapStateToProps,mapDispatchToProps)(ShopPage)
